import React from 'react';
import styled from 'styled-components';
import HeroBanner from './components/HeroBanner';
import Kudos from './components/Kudos';
import About from './components/About';
import PhoneShowcase from './components/PhoneShowcase';
import PhoneParamsSelect from './components/PhoneParamsSelect';
import {
  phoneWidth,
  phoneHeight,
  phoneColor,
  phoneSecondaryColor,
  dark,
  cameraColor as camColor,
  reactDefaultBackground,
} from './components/constants';

const Container = styled.div`
  margin: 0;
  padding: 0;
  padding-bottom: 100px;
  display: flex;
  flex-direction: column;
  max-width: 100vw !important;
  overflow: hidden;
`;

const Heading = styled.h2`
  text-align: left;
  margin-left: 80px;
`;

const ResultWrapper = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
`;

const Code = styled.code`
  padding: 10px 10px;
  font-size: 14px;
  border-radius: 15px;
  background-color: #f3f3f3;
  width: 100%;
  text-align: left;

  @media (min-width: 768px) {
    font-size: 16px;
    padding: 30px 30px;
  }
`;

const CodeBlock = styled.p`
  margin-top: 0;
  margin-bottom: 0;
`;

interface PhoneParams {
  [key: string]: string;
}

function App() {
  const [caseColor, setCaseColor] = React.useState<string>(phoneColor);
  const [screenColor, setScreenColor] = React.useState<string>(
    reactDefaultBackground
  );
  const [shadowColor, setShadowsColor] = React.useState<string>(
    phoneSecondaryColor
  );
  const [detailsColor, setDetailsColor] = React.useState<string>(dark);
  const [cameraColor, setCameraColor] = React.useState<string>(camColor);
  const [volumeButtonsColor, setVolumeButtonsColor] = React.useState<string>(
    phoneSecondaryColor
  );
  const [width, setWidth] = React.useState<number>(phoneWidth);
  const [height, setHeight] = React.useState<number>(phoneHeight);

  const phoneParams: PhoneParams = {
    caseColor,
    screenColor,
    detailsColor,
    shadowColor,
    cameraColor,
    volumeButtonsColor,
    width: width + 'px',
    height: height + 'px',
  };

  return (
    <Container>
      <PhoneShowcase phoneParams={phoneParams} />
      <HeroBanner />
      <About />

      <Heading>Customize!</Heading>
      <PhoneParamsSelect
        width={width}
        height={height}
        setWidth={setWidth}
        setHeight={setHeight}
        setCaseColor={setCaseColor}
        setScreenColor={setScreenColor}
        setShadowsColor={setShadowsColor}
        setDetailsColor={setDetailsColor}
        setCameraColor={setCameraColor}
        setVolumeButtonsColor={setVolumeButtonsColor}
      />

      <ResultWrapper>
        <Heading>Result</Heading>
        <Code>
          <CodeBlock>{`
        <SmartphoneContainer`}</CodeBlock>
          <span>
            {`
          height='${height + 'px'}' // regular height values px/%/vh/vw...
          width='${width + 'px'}' // regular width values px/%/vh/vw...
          caseColor='${screenColor}' // hex or rgb
          detailsColor='${detailsColor}' // hex or rgb
          cameraColor='${cameraColor}' // hex or rgb
          shadowColor='${shadowColor}' // hex or rgb
          volumeButtonsColor='${volumeButtonsColor}' // hex or rgb
          screenColor='${screenColor}' // hex or rgb
          >`
              .split('\n')
              .map((row) => (
                <CodeBlock key={row} style={{ marginLeft: '25px' }}>
                  {row}
                </CodeBlock>
              ))}
          </span>
          <p style={{ margin: '15px 25px', fontWeight: 'bolder' }}>
            // Your component goes here!
          </p>
          <CodeBlock>
            {`
        </SmartphoneContainer>
        `}
          </CodeBlock>
        </Code>
      </ResultWrapper>

      <Kudos />
    </Container>
  );
}

export default App;
